import { ReactNode } from 'react';
import classNames from 'classnames';
import { SettingsButton } from '../transforming-buttons';
import { Avatar } from '../avatar';

import styles from './info-container.module.css';
import placeholder from './img/placeholder.svg';
import useUser from 'shared/hooks/use-user';
import { DefaultAvatar } from 'entities/task/ui/task/img/default-avatar';
import { useAppSelector } from '../../../app/hooks';
import { isUserBlockedSelector } from '../../../entities/user/model';

interface InfoContainerProps {
  extClassName?: string;
  children?: ReactNode;
  avatar?: string;
  name: string;
  onClickSettingsButton?: () => void;
}

export const InfoContainer = ({
  extClassName,
  children,
  avatar,
  name,
  onClickSettingsButton,
}: InfoContainerProps) => {
  const isAuth = useUser();
  const isBlockedSelector = useAppSelector(isUserBlockedSelector);

  return (
    <div className={classNames(styles['info-container-frame'], extClassName)}>
      <img
        className={styles['info-container-border']}
        src="data:image/png;base64,"
        alt="Граница профиля"
      />
      <div className={styles['info-container-avatarWrapper']}>
        {avatar ? (
          <Avatar
            avatarLink={avatar || placeholder}
            avatarName={name}
            extClassName={styles['info-container-avatar']}
          />
        ) : (
          <DefaultAvatar isTaskAvatar={false} />
        )}
      </div>
      <div className={styles['info-container-content']}>{children}</div>
      {isAuth && !isBlockedSelector && (
        <SettingsButton
          extClassName={styles['info-container-settings-button']}
          onClick={onClickSettingsButton}
          disabled={!isAuth}
        />
      )}
    </div>
  );
};
